export class StreamingSummarizer{constructor(t){this.outputElement=t,this.currentText="",this.isStreaming=!1}appendText(t){this.currentText+=t,this.render()}render(){this.outputElement.innerHTML=`\n            <div class="summary-content">\n                ${this.formatText(this.currentText)}\n                <span class="streaming-cursor">▌</span>\n            </div>\n        `,this.outputElement.scrollTop=this.outputElement.scrollHeight}formatText(t){return t.replace(/\*\*(.*?)\*\*/g,"<strong>$1</strong>").replace(/\n/g,"<br>").replace(/^•\s+(.*)$/gm,"<li>$1</li>")}clear(){this.currentText="",this.outputElement.innerHTML='<p class="placeholder">Your summary will appear here...</p>'}}